import './App.css';
import {ReactComponent as Icon} from './Components/Atoms/Icon/icon.svg'
import TypoIcon from './Components/Molecules/TypoIcon';
import Tag from './Components/Atoms/tag';
import styled from 'styled-components';
import Typography from '@mui/material/Typography'




function App() {
  const monthNames = ["January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December"
];

const d = new Date("2023-05-03");
const Month= monthNames[d.getMonth()]
const Year=d.getFullYear()
const Date_1=d.getDate()
const s="Due - "+Month+" "+Date_1+", "+Year
  return (
    <div className="AppDiv">
      <div className='InDiv'>
      <Icon ></Icon>
      <div className='TextDiv'>
      <TypoIcon>{s}</TypoIcon>
      <Typography variant='h6' color='#E8E7F0'>$14,204.55</Typography>
      </div>
      </div>
      <Tag data={d}></Tag>
    </div>
  );
}

export default App;
