import Typography from '@mui/material/Typography'
import styled from 'styled-components'
import './index.css'



const Tag:React.FC<{due:Date}>=(props)=>{
    const {due}=props
    const monthNames = ["January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];
  
  const d = new Date();
  const Month=d.getMonth()
  const Year=d.getFullYear()
  const Day=d.getDate()
  const format =Month+'/'+Day+'/'+Year;
  const temp = new Date(format);
  const diff= due.getTime() - temp.getTime(); 
  const No_Days=  diff / (1000 * 60 * 60 * 24);  

    return(
<div className='style'>
    <Typography variant='body2'>Due in {No_Days} day(s)</Typography>
</div>
    )
}

export default Tag
